[{"fileName":"loopsQ_hw_1-21-24.py","_id":"cebd5841-de96-4cc6-a1cc-bf56687ffe5c","code":"# Q 6 HW\n\nprint(\"Total Saving\")\nbob = 0\n\nwhile True:\n    loop = input(\"How much money did you save this month? (type '0' to stop): \")\n    if loop != '0':\n        bob += int(loop)  # Convert loop to integer before adding\n    elif loop == '0':\n        break\n\nprint(f\"Your total savings were : {bob}\")\n\n\n\n## Q 17 HW\n\nprint(\"TO-DO List\")\ntodo = []\nwhile True:\n    loop = input(\"Enter items for your To-Do List (Type 'done' to stop): \")\n    if loop != 'done':\n        todo.append(loop)\n    elif loop == 'done':\n        break\nprint(todo)"}]